    private void btnSubmitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubmitActionPerformed
        String email = txtEmail.getText();
        String password = new String(txtPassword.getPassword());

        UserAuthController auth = new UserAuthController();
        User user = auth.login(email, password);

        if (user == null) {
            txtErrorOrSuccessMessage.setText(auth.getMensagemErro());
            return;
        }

        app.setUser(user);
        app.showHomepage();
    }//GEN-LAST:event_btnSubmitActionPerformed

    private void txtbtnRegisterNavigateMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_txtbtnRegisterNavigateMouseReleased
        app.showRegister();
    }//GEN-LAST:event_txtbtnRegisterNavigateMouseReleased


